import { useState } from "react";

import "./AuthPage.css"; // Import the CSS file
import React from "react";

export default function AuthPage() {
  const [isSignup, setIsSignup] = useState(false);
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [username, setUsername] = useState("");
  const [userType, setUserType] = useState("buyer");

  const handleAuth = () => {
    console.log(
      isSignup ? "Signing up" : "Logging in",
      email,
      password,
      username,
      userType
    );
  };

  return (
    <div className="auth-container">
      <h2>{isSignup ? "Create an Account" : "Welcome Back"}</h2>
      <p>
        {isSignup
          ? "Sign up to start bidding and selling!"
          : "Secure login to your account"}
      </p>

      {isSignup && (
        <div className="input-group">
          
          <input
            type="text"
            placeholder="Username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
          />
        </div>
      )}

      <div className="input-group">
        
        <input
          type="email"
          placeholder="Email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
        />
      </div>

      <div className="input-group">
        
        <input
          type="password"
          placeholder="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />
      </div>

      {isSignup && (
        <div className="input-group">
          <select
            value={userType}
            onChange={(e) => setUserType(e.target.value)}
          >
            <option value="buyer">Buyer</option>
            <option value="seller">Seller</option>
          </select>
        </div>
      )}

      <div className="action-links">
        {!isSignup && <a href="#">Forgot Password?</a>}
        <a href="#" onClick={() => setIsSignup(!isSignup)}>
          {isSignup ? "Already have an account? Login" : "New here? Sign Up"}
        </a>
      </div>

      <button onClick={handleAuth} className="auth-btn">
        {isSignup ? "Sign Up" : "Login"}
      </button>

      <div className="oauth-buttons">
        <button>
           Google
        </button>
        <button>
          <FaFacebook /> Facebook
        </button>
      </div>

      <p className="security-msg">
        ðŸ”’ Secure authentication with SSL encryption
      </p>
    </div>
  );
}
